#!/bin/bash
ssh -i /home/ec2-user/.pem/bootdev.pem -o 'StrictHostKeyChecking no' -t ec2-user@<%= @ChefServerIP %> "cd /home/ec2-user/chef11/chef-repo;sudo chmod 777 .chef;sudo -u root knife vault show secrets secret_key 'secret_key' --mode client --format json > .chef/secret_key.json;sudo -u root knife vault update secrets secret_key --json .chef/secret_key.json --search 'name:*' --mode client;sudo -u root rm .chef/secret_key.json;sudo chmod 744 .chef"
